\begin{equation}
\begin{split}
\Delta(x) = & \frac{gL^2}{4 \pi^2}\int_0^{2\pi/L} d p_x \int_0^{p_c} d p_y \sum_i\sum_n \\
& \times v_{i,n,\mathbf p}^*(x)u_{i,n\mathbf p}(x) \tanh\left[\frac{E_n({\mathbf p})}{2 k_B T}\right],
\end{split}
\label{eq:selfcons}
\end{equation}

