\begin{eqnarray}\label{eq:algebra_3}
\left[ \mathcal{L}_0 - \sum\limits_{k = k_0}^{2b}y_k\Omega_{k;m}\right] x = U,\\
\label{eq:algebra_3bis}
\mathcal{L}_0 x = U + \sum\limits_{k = k_0}^{2b}y_k\Omega_{k;m} x.
\end{eqnarray}

Assume now that :

\begin{eqnarray}
x^{(0)} &=& \mathcal{L}_0^{-1} U, \label{eq:algebra_4}\\
x^{(k-k_0+1)} &=& y_k \mathcal{L}_0^{-1} \Omega_{k;m} x,\,\,\, k = k_0,...,2b  \label{eq:algebra_4_2}
\end{eqnarray}
and sum up, side by side, the equations:
\begin{eqnarray}\label{eq:algebra_5}
x^{(0)} + \sum\limits_{k = k_0}^{2b} x^{(k-k_0+1)} = \mathcal{L}_0^{-1} \left[ U + \sum\limits_{k = k_0}^{2b} y_k \Omega_{k;m} x \right], \\
\label{eq:algebra_6}
 \mathcal{L}_0 \left(x^{(0)} + \sum\limits_{k = k_0}^{2b} x^{(k-k_0+1)} \right) = U + \sum\limits_{k = k_0}^{2b} y_k \Omega_{k;m} x .
\end{eqnarray}
