\begin{equation}
\rho_A(\lambda) = \frac{1}{\pi N}~\lim_{\varepsilon\to 0} {\rm Im~Tr~} (\lambda_{\varepsilon} {\mathbb{I}} - A)^{-1}
= -\frac{2}{\pi N}~\lim_{\varepsilon\to 0} {\rm Im}~\frac{\partial}{\partial \lambda} \ln Z_N\ ,
\label{rho-res}
\end{equation}

\begin{equation}
Z_N = \int \prod_{i=1}^N \frac{{\mathrm d} u_i}{\sqrt{2\pi/{\mathrm i}}}~\exp\Big\{
-\frac{{\mathrm i}}{2}\sum_{i,j}  \big(\lambda_{\varepsilon} \delta_{ij} - 
A_{ij}\big)\ u_i u_j\Big\}\ .
\label{ZN}
\end{equation}

\begin{equation}
P(u_i) \propto \exp\Big\{-\frac{{\mathrm i}}{2}\lambda_{\varepsilon}\, u_i^2\Big\}
\int \prod_{j\in \partial i} {\mathrm d} u_j \, \exp\Bigg\{{\mathrm i} \sum_{j\in \partial i}
K_{ij} u_i u_j\Bigg\} P_j^{(i)}(u_j)\ ,
\label{ss-marg}
\end{equation}

\begin{equation}
P_j^{(i)}(u_j) \propto \exp\Big\{-\frac{{\mathrm i}}{2}\lambda_{\varepsilon}\, 
u_j^2\Big\} \prod_{\ell\in \partial j\setminus i} \int {\mathrm d} u_\ell \, 
\exp\Big\{{\mathrm i} K_{j\ell} u_j u_\ell\Big\} P_\ell^{(j)}(u_\ell)\ .
\label{ss-cmarg}
\end{equation}
