&& \ddot{w}_1=\frac{4 \dot{w}_1 \dot{z}_2+4 \dot{w}_2 \dot{z}_1+6 w_1 w_2}{z_1-z_2}+\frac{4 \dot{w}_1 \dot{z}_3+4 \dot{w}_3 \dot{z}_1+6 w_1 w_3}{z_1-z_3}\notag\\
&&-\frac{1}{(z_1-z_2)(z_1-z_3)}\Bigg\{
12\left[ w_1\dot{z}_2 \dot{z}_3+\dot{z}_1(w_2 \dot{z}_3+w_3 \dot{z}_2) \right]+z_1^2 K_1(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})\notag\\
&&+ z_1 K_2(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})+ K_3(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})
\Bigg\},\notag\\
&& \ddot{w}_2=\frac{4 \dot{w}_2 \dot{z}_1+4 \dot{w}_1 \dot{z}_2+6 w_1 w_2}{z_2-z_1}+\frac{4 \dot{w}_2 \dot{z}_3+4 \dot{w}_3 \dot{z}_2+6 w_2 w_3}{z_2-z_3}\notag\\
&&-\frac{1}{(z_2-z_1)(z_2-z_3)}\Bigg\{
12\left[ w_2\dot{z}_1 \dot{z}_3+\dot{z}_2(w_1 \dot{z}_3+w_3 \dot{z}_1) \right]+z_2^2 K_1(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})\notag\\
&&+ z_2 K_2(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})+ K_3(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})
\Bigg\},\notag\\
&& \ddot{w}_3=\frac{4 \dot{w}_3 \dot{z}_1+4 \dot{w}_1 \dot{z}_3+6 w_1 w_3}{z_3-z_1}+\frac{4 \dot{w}_3 \dot{z}_2+4 \dot{w}_2 \dot{z}_3+6 w_2 w_3}{z_3-z_2}\notag\\
&&-\frac{1}{(z_3-z_1)(z_3-z_2)}\Bigg\{
12\left[ w_3\dot{z}_1 \dot{z}_2+\dot{z}_3(w_1 \dot{z}_2+w_2 \dot{z}_1) \right]+z_3^2 K_1(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})\notag\\
&&+ z_3 K_2(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})+ K_3(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})
\Bigg\},\notag\\
\end{eqnarray}
where
\begin{eqnarray}
&& K_1(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})=-\Big[ 
\alpha_1(\dot{w}_1+\dot{w}_2+\dot{w}_3)+\beta_1(w_1+w_2+w_3)\notag\\
&&+\gamma_1(\dot{z}_1+\dot{z}_2+\dot{z}_3)+\delta_1(z_1+z_2+z_3),
\end{eqnarray}
\begin{eqnarray}
 && K_2(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})=
\alpha_2\Big[
\dot{w}_1(z_2+z_3)+\dot{w}_2(z_1+z_3)+\dot{w}_3(z_1+z_2)+2 w_1(\dot{z}_2+\dot{z}_3)\notag\\
&&+2 w_2(\dot{z}_1+\dot{z}_3)+2 w_3(\dot{z}_1+\dot{z}_2)+\dot{z}_1(w_2+w_3)+\dot{z}_2(w_1+w_3)+\dot{z}_3(w_1+w_2)\Big]\notag\\
&&+\beta_2\Big[
w_1(z_2+z_3)+w_2(z_1+z_3)+w_3(z_1+z_2)+\dot{z}_1(\dot{z}_2+\dot{z}_3)+\dot{z}_2(\dot{z}_1+\dot{z}_3)\notag\\
&&+\dot{z}_3(\dot{z}_1+\dot{z}_2)
\Big]+\gamma_2\Big[ 
\dot{z}_1(z_2+z_3)+\dot{z}_2(z_1+z_3)+\dot{z}_3(z_1+z_2)
\Big]\notag\\
&&+\delta_2(z_1 z_2+z_1 z_3+z_2 z_3),
\end{eqnarray}
\begin{eqnarray}
&& K_3(\vec{z}, \dot{\vec{z}}, \vec{w}, \dot{\vec{w}})=-\Bigg\{
\alpha_3\Big[
\dot{w}_1 z_2 z_3+\dot{w}_2 z_1 z_3+\dot{w}_3 z_1 z_2+ 2w_1(\dot{z}_2 z_3 + z_2 \dot{z}_3) \notag\\
&&+2w_2(\dot{z}_1 z_3 + z_1 \dot{z}_3)+ 2w_3(\dot{z}_1 z_2 + z_1 \dot{z}_2)
+\dot{z}_1(w_2 z_3+2 \dot{z}_2 \dot{z}_3 + z_2 w_3)\notag\\
&&+\dot{z}_2(w_1 z_3+2 \dot{z}_1 \dot{z}_3 + z_1 w_3)
+\dot{z}_3(w_1 z_2+2 \dot{z}_1 \dot{z}_2 + z_1 w_2)
\Big]\notag\\
&&+\beta_3\Big[
w_1 z_2 z_3 +w_2 z_1 z_3 +w_3 z_1 z_2 +\dot{z}_1(\dot{z}_2 z_3+ z_2 \dot{z}_3)+\dot{z}_2(\dot{z}_1 z_3 + z_1 \dot{z}_3)\notag\\
&&+\dot{z}_3 (\dot{z}_1 z_2+ z_1 \dot{z}_2)
\Big]
+\gamma_3\Big[
\dot{z}_1 z_2 z_3+ z_1 \dot{z}_2 z_3+ z_1 z_2 \dot{z}_3
\Big]+\delta_3 z_1 z_2 z_3
\Bigg\}.
\end{eqnarray}
\label{SystemzwN3}
\end{subequations}

