\begin{align}
\int_{-1}^{+1} \mathrm{d}\, x \ x^{n} \, H^q(x, \xi, t) &= 
\sum \limits_{i=0}^{n}  \delta_{i, \mathrm{even}} 
(-2\xi)^i A^q_{n+1, i}(t) +  \delta_{n, \mathrm{odd}} (-2\xi)^{n+1} \,  C^q_{n+1}(t), \\ 
\int_{-1}^{+1} \mathrm{d}\, x \ x^{n} \, E^q(x, \xi, t) &= 
\sum \limits_{i=0}^{n}  \delta_{i, \mathrm{even}} 
(-2\xi)^i B^q_{n+1, i}(t) -  \delta_{n, \mathrm{odd}} (-2\xi)^{n+1} \,  C^q_{n+1}(t).
\end{align}

