\section {The order $\trianglelefteq^{{\rm rp}}_\lambda$} \label{5}
\bigskip

\subsection {On $\trianglelefteq^{{\rm rp}}_\lambda$}\
\bigskip

\begin{convention}
\label{s2}
$T$ denotes a complete first order theory and, if not said otherwise
with elimination of quantifiers.
\end{convention}

\noindent
Recall (from \ref{z6}).
\begin{definition}
\label{s4}

\noindent
1) Let $T_1 \trianglelefteq^{{\rm rp}}_{\lambda,\mu} T_2$ means: if $D$ is
a regular filter on $\lambda$ and $M_\ell$ is a model of $T_\ell$ for
$\ell=1,2$ and $(M_2)^\lambda/D$ is ($\mu^+$,atomic)-saturated,
{\underline{then}} \, so is $(M_1)^\lambda/D$.

\noindent
1A) Writing $\triangleleft$ instead of $\trianglelefteq$ means that
$(T_1 \trianglelefteq^{{\rm rp}}_{\lambda,\mu} T_2) \wedge \neg(T_2
\trianglelefteq^{{\rm rp}}_{\lambda,\mu})$.

\noindent
2) Omitting $\mu$ means $\mu = \lambda$.

\noindent
3) Omitting $\lambda,\mu$ means for every $\lambda$, may write
$\trianglelefteq_{{\rm rp}}$.  

\noindent
4) Similarly for $(T_1,\Delta_1) \trianglelefteq^{{\rm rp}}_{\lambda,\mu}
(T_2,\Delta_2)$. 

\noindent
5) Let $(T_2,*) \trianglelefteq^{{\rm rp}}_{\lambda,\mu} (T_2,*)$ means
that for every finite set $\Delta_1$ of atomic formulas of
${\mathbb L}(\tau(T_2))$, there is a finite set $\Delta_2$ of atomic formulas
from ${\mathbb L}(\tau(T_2))$ such that $(T_1,\Delta_1)
\trianglelefteq^{{\rm rp}}_{\lambda,\mu} (T_2,\Delta_2)$.
\end{definition}

\noindent
Of course
\begin{fact}
\label{s8}
1) In \ref{s4} the choices of $M_1,M_2$ does not matter.

\noindent
2) $\trianglelefteq^{{\rm rp}}_{\lambda,\mu},\trianglelefteq^{{\rm rp}}_\lambda,
\trianglelefteq_{{\rm rp}}$ are quasi order.

\noindent
3) Assume $M_2 = M^{{\rm eq}}_1$, see \cite[Ch.III]{Sh:c} and $T_\ell =
{\rm Th}(M_\ell)$ for $\ell=1,2$ and $T_1$ with elimination of quantifiers, 
{\underline{then}} \, $T_1,T_2$ are
$\trianglelefteq^{{\rm rp}}$-equivalent. 
\end{fact}

\begin{PROOF}{\ref{s8}}
1) Because in \ref{s4} the filter $D$ is regular (see $\boxplus$ from
\ref{b10}). 

\noindent
2) Should be clear.
\end{PROOF}

\begin{claim}
\label{s13}
If $T$ has ${\rm SOP}_3$ for quantifier free formulas 
{\underline{then}} \, $T$ is $\trianglelefteq^{{\rm rp}}_\lambda$-maximal.
\end{claim}

\begin{PROOF}{\ref{s13}}
By \S1,\S2.
\end{PROOF}

