\end{equation}

\begin{equation}
w = E -E_{\rm s} = m_0 c^2 ( \gamma - \gamma_{\rm s} ) = m_0 c^2 \,{\mathrm{d}}\gamma = m_0 c^2 \gamma_{\rm s}^3 \beta_{\rm s} \, {\mathrm{d}}\beta = m_0 \gamma_{\rm s}^3 v_{\rm s} (v - v_{\rm s}),
