\alpha \, b(r)\equiv \alpha \left(
{1 \over r} - \int d^3r_1 \;{\vert\phi_{_{\rm M1}}(r_1)\vert^2\over \vert \vec r - \vec r_1\vert}\right).
\label{IP}
\end{equation}

\begin{equation}
{dR_P\over dE_c}=\int_0^Q \! dE_1\int_0^Q \! dE_2\, {d\hat R\over dE_1}\,{d\hat R\over dE_2}
\,\delta(E_c-E_1-E_2).
\label{pileup}
