	\hat H_{ J}= -\frac{E_{ J}}{2}\left(
	  e^{2i eV_{\rm dc}t}\hat D[\alpha (t)]+e^{-2i eV_{\rm dc}t}\hat D^\dagger[\alpha (t)]
	  \right),
\end{equation}

\begin{equation}\label{eq:displacement}
	\hat D[ \alpha (t)] = e^{\alpha (t)\hat a^{\dagger}-\alpha^*(t)\hat a},\,\,
	\alpha (t)=	2\lambda e^{i \omega_{\rm c} t}.
\end{equation}

\begin{multline}\label{eq:DT}
