\begin{equation}
ds^2 = - a^2(r,t) dt^2 + b^2(r,t) dr^2 + R^2(r,t)d\Omega^2 \ .
\label{eq_metric_MS}
\end{equation}

\begin{align}
 &U \equiv D_t R = \frac{1}{a} \partial_t R \ ,  \label{U_def} \\ 
 &\Gamma \equiv D_r R = \frac{1}{b} \partial_r R \label{Gamma_def} \ ,
\end{align}

\begin{equation}
    a^2 dt^2 = b^2 dr^2  \phantom{sp} \text{giving} \phantom{sp} 
      \frac{\dif r}{\dif t} = \pm \frac{a}{b} \ ,
\end{equation}

\begin{equation}
 \dif R = \frac{\partial R}{\partial t}\,\dif t 
    + \frac{\partial R}{\partial r}\,\dif r \,, 
\label{RWL}
\end{equation}

\begin{equation}
 \left.\frac{1}{a}\frac{\dif R}{\dif t}\right\vert_{\pm} = 
   \left(\frac{1}{a}\frac{\partial R}{\partial t} \pm 
     \frac{1}{b}\frac{\partial R}{\partial r}\right) = 
     \left(D_t R \pm D_r R\right) = \left(U \pm \Gamma \right) \ ,
\label{RNR2}
\end{equation}

\begin{align}
 \alpha=\frac{\mathcal{L}_{+} \theta_+}{\mathcal{L}_{-} \theta_+} = \frac{(D_t + D_r) \theta_+}{(D_t - D_r) \theta_+} = \frac{(D_t U + D_t \Gamma) + (D_r U + D_r \Gamma)}{(D_t U + D_t \Gamma) - (D_r U + D_r \Gamma)}\ ,
 \label{alpha_BH}
\end{align}

\begin{align}
 \alpha=\frac{\mathcal{L}_{-} \theta_-}{\mathcal{L}_{+} \theta_-} = \frac{(D_t - D_r) \theta_-}{(D_t + D_r) \theta_-} = \frac{(D_t U - D_t \Gamma) - (D_r U - D_r \Gamma)}{(D_t U - D_t \Gamma) + (D_r U - D_r \Gamma)}\ .
 \label{alpha_CH}
\end{align}

\begin{align}
 v_{tot} & \equiv \frac{dR}{dt} = \dot{s}r + s\dot{r} = U + \Gamma v \nonumber \\
               & = HR - \sqrt{1-kr^2}\,c  = v_{rec} + v_{pec} \ ,
 \label{eq_areal_velocity_cosmo}
\end{align}
