\begin{equation} \label{E:Eq-1}
\mathcal{M}': z(u,v) = \left( x_1(u), x_2(u), r(u) \cos v, r(u) \sin v\right);
\quad u \in J,\,\,  v \in [0; 2\pi).
\end{equation}

\begin{equation} \label{E:Eq-2}
\mathcal{M}'': z(u,v) = \left(r(u) \cosh v,  x_2(u), r(u) \sinh v, x_4(u) \right);
\quad u \in J,\,\,  v \in {\mathbb R}.
\end{equation}

\begin{equation} \label{E:Eq-3}
\mathcal{M}''': z(u,v) = x_1(u)\, e_1 + f(u) \, \xi_1 + (-v^2 f(u) + g(u)) \, \xi_2 + \sqrt{2}\, v f(u) \, e_4;
\; u \in J,\,\,  v \in {\mathbb R}.
\end{equation}

\begin{equation} \label{E:Eq-5}
\begin{array}{l}
n_1 = {\displaystyle}{\frac{1}{\sqrt{1+(r')^2}}\left(- x_2', x_1',0,0 \right)};\\
n_2 = {\displaystyle}{\frac{1}{\sqrt{1+(r')^2}} \left(r' x_1', r' x_2', (1+(r')^2) \cos v, (1+(r')^2) \sin v \right)}.
\end{array}
\end{equation}

\begin{equation*} \label{E:Eq-7}
\begin{array}{l}
\vspace{1mm}
\sigma(X,X)= {\displaystyle}{\frac{x_1' x_2'' - x_1'' x_2'}{\sqrt{1+(r')^2}} \, n_1 + \frac{r''}{\sqrt{1+(r')^2}}\, n_2},\\
\vspace{1mm}
\sigma(X,Y)= 0,\\
\vspace{1mm}
\sigma(Y,Y) =\qquad \qquad \qquad \quad \; - {\displaystyle}{\frac{\sqrt{1+(r')^2}}{r}\, n_2},
\end{array}
\end{equation*}

\begin{equation}  \label{E:Eq-8}
H = \frac{1}{2 r\sqrt{1+(r')^2}} \left( r(x_1' x_2'' - x_1'' x_2') \, n_1 + (r r'' + (r')^2 + 1) \, n_2 \right).
\end{equation}

\begin{equation} \label{E:Eq-9}
\begin{array}{l}
\vspace{2mm}
\nabla'_X n_1 =- {\displaystyle}{\frac{x_1' x_2'' - x_1'' x_2'}{\sqrt{1+(r')^2}} \, X + \frac{r'}{1+(r')^2}(x_1' x_2'' - x_1'' x_2')}\, n_2, \\
\vspace{2mm}
\nabla'_Y n_1 = 0,\\
\vspace{2mm}
\nabla'_X n_2 ={\displaystyle}{\frac{r''}{\sqrt{1+(r')^2}} \, X + \frac{r'}{1+(r')^2}(x_1' x_2'' - x_1'' x_2') \, n_1}, \\
\vspace{2mm}
\nabla'_Y n_2 = {\displaystyle}{\frac{\sqrt{1+(r')^2}}{r}\, Y}. 
\end{array}
\end{equation}

\begin{equation} \label{E:Eq-11}
{\displaystyle}{\frac{r^2 (x_1' x_2'' - x_1'' x_2')^2 - (r r'' + (r')^2 + 1)^2}{4r^2(1+(r')^2)}}= \varepsilon C^2,\; \varepsilon = sign \langle H, H \rangle, \; C = const \neq 0.
\end{equation}

\begin{equation} \label{E:Eq-5a}
\begin{array}{l}
\vspace{2mm}
n_1 = {\displaystyle}{\frac{1}{\sqrt{\varepsilon((r')^2 - 1)}}\left(0, x_4', 0, x_2' \right)};\\
\vspace{2mm}
n_2 = {\displaystyle}{\frac{1}{\sqrt{\varepsilon((r')^2 - 1)}} \left((1-(r')^2) \cosh v, - r' x_2',  (1-(r')^2) \sinh v, - r' x_4' \right)}.
\end{array}
\end{equation}

\begin{equation*}  \label{E:Eq-8a}
H = {\displaystyle}{\frac{\varepsilon}{2 r\sqrt{\varepsilon((r')^2 - 1)}} \left( r(x_4' x_2'' - x_4'' x_2') \, n_1 - (r r'' + (r')^2 - 1) \, n_2 \right)}.
\end{equation*}

\begin{equation*}  \label{E:Eq-8b}
\langle H, H \rangle = {\displaystyle}{\frac{1}{4 f^2 f'^2} \left( f^2(x_1'' f' - x_1' f'')^2 - (f f'' + (f')^2)^2 \right)}.
\end{equation*}

