\delta_{n,m}(x,y)=\frac{1}{2}\max\left(\frac{p_{1}^{n-1}(x-x^{2})}{[n]_{p_{1},q_{1}}},\frac{p_{2}^{m-1}(y-y^{2}}{[m]_{p_{2},q_{2}}}\right).
\end{eqnarray*}
\end{thm}


\begin{proof}
Let $g \in C^{2}([0,1]^{2})$. By the Taylor's formula, we get
\begin{eqnarray*}
g(s_{1},s_{2})-g(x,y)&=&g(s_{1},y)-g(x,y)+g(s_{1},s_{2})-g(s_{1},y)\\
&=&\frac{\partial g(x,y)}{\partial
x}(s_{1}-x)+\int_{x}^{s_{1}}(s_{1}-u)\frac{\partial^{2}
g(u,y)}{\partial u^{2}}du\\
&&+\frac{\partial g(x,y)}{\partial
x}(s_{2}-y)+\int_{y}^{s_{2}}(s_{2}-v)\frac{\partial^{2}
g(x,v)}{\partial v^{2}}dv\\
&=&\frac{\partial g(x,y)}{\partial
x}(s_{1}-x)+\int_{0}^{s_{1}-x}(s_{1}-x-u)\frac{\partial^{2}
g(u,y)}{\partial u^{2}}du\\
&&+\frac{\partial g(x,y)}{\partial
x}(s_{2}-y)+\int_{0}^{s_{2}-y}(s_{2}-y-v)\frac{\partial^{2}
g(x,v)}{\partial v^{2}}dv
\end{eqnarray*}
Applying $B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}$ to the both sides
of the above equation, we obtain
\begin{eqnarray*}
\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(g;x,y\right)-g(x,y)\right|&{\leqslant}&\left|\frac{\partial
g(x,y)}{\partial x}\right|\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{1}-x);x,y\right)\right|\\
&&+\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(\int_{0}^{s_{1}-x}(s_{1}-x-u)\frac{\partial^{2}
g(u,y)}{\partial u^{2}}du;x,y\right)\right|\\
&&+\left|\frac{\partial
g(x,y)}{\partial y}\right|\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{2}-y);x,y\right)\right|\\
&&+\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(\int_{0}^{s_{2}-y}(s_{2}-y-v)\frac{\partial^{2}
g(v,x)}{\partial v^{2}}dv;x,y\right)\right|
\end{eqnarray*}
As
$B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{1}-x);x,y\right)=0$
and
$B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{2}-y);x,y\right)=0$,
one can write following
\begin{eqnarray*}
\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(f;x,y\right)-f(x,y)\right\|_{C\left([
0,1]^{2} \right)}&{\leqslant}& \frac{1}{2}\left\|\frac{\partial
g(x,y)}{\partial x}\right\|_{C\left([ 0,1]^{2}
\right)}\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{1}-x)^{2};x,y\right)\right|\\
&&+\frac{1}{2}\left\|\frac{\partial g(x,y)}{\partial
y}\right\|_{C\left([ 0,1]^{2}
\right)}\left|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left((s_{2}-y)^{2};x,y\right)\right|.
\end{eqnarray*}
By \eqref{moment1}, \eqref{moment2}, we deduce,

\begin{eqnarray}\label{ali1}
\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(f;x,y\right)-f(x,y)\right\|_{C\left([
0,1]^{2}
\right)}&{\leqslant}&\frac{1}{2}\max\left(\frac{p_{1}^{n-1}(x-x^{2})}{[n]_{p_{1},q_{1}}},\frac{p_{2}^{m-1}(y-y^{2}}{[m]_{p_{2},q_{2}}}\right)\nonumber\\
&&\times\left[\left\|\frac{\partial g(x,y)}{\partial
x}\right\|_{C\left([ 0,1]^{2} \right)}+\left\|\frac{\partial
g(x,y)}{\partial x}\right\|_{C\left([ 0,1]^{2} \right)}\right]\nonumber\\
&{\leqslant}&\parallel g\parallel_{C\left([ 0,1]^{2} \right)}\delta_{n,m}.
\end{eqnarray}
By the linearity $B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}$, we obtain
\begin{eqnarray}\label{ali2}
\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(f;x,y\right)-f(x,y)\right\|_{C\left([
0,1]^{2}
\right)}&{\leqslant}&\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}f-B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}g\right\|_{C\left([
0,1]^{2}
\right)}\nonumber\\&&+\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}g-g\right\|_{C\left([
0,1]^{2} \right)}+\left\|f-g\right\|_{C\left([ 0,1]^{2} \right)}.
\end{eqnarray}
By \eqref{ali1} and \eqref{ali2}, one can see that
\begin{eqnarray*}
\left\|B^{(p_{1},q_{1}),(p_{2},q_{2})}_{n,m}\left(f;x,y\right)-f(x,y)\right\|_{C\left([
0,1]^{2} \right)}\leq2M\left(f;\delta_{n,m}(x,y)/2 \right).
\end{eqnarray*}
This step completes the proof.
\end{proof}


