\end{enumerate}
\end{lem}
\begin{proof}
From \eqref{moment1}, we get
\begin{eqnarray*}
[n]_{p_{n},q_{n}}
B^{(p_{n},q_{n})}_{n}((t-x)^{2};x)=-p_{n}^{n-1}x^{2}+xp_{n}^{n-1}.
\end{eqnarray*}
Let us take the limit of both sides of the above equality as
$n\longrightarrow \infty$, then we have
\begin{eqnarray*}
\lim_{n \to \infty}[n]_{p_{n},q_{n}}
\left\{B^{(p_{n},q_{n})}_{n}((t-x)^{2},x)\right\}&=&\lim_{n \to
\infty}
\left\{-p_{n}^{n-1}x^{2}+xp_{n}^{n-1}\right\}\\
&=&a(x-x^{2}).
\end{eqnarray*}

(ii) Using Lemma \ref{lemma1},  Lemma \ref{lemma2}  and by the
linearity of the operators $B^{(p_{n},q_{n})}_{n}(f;x)$, we obtain
\begin{eqnarray*} \
B^{(p_{n},q_{n})}_{n}(t-x)^{4};x)=A_{1,n}x^{4}+A_{2,n}x^{3}+A_{3,n}x^{2}+A_{4,n}x
\end{eqnarray*}
where
\begin{eqnarray*}
A_{1,n}&=&\frac{p_{n}^{n-3}[n]^{2}_{p_{n},q_{n}}(-p_{n}^{2}+2p_{n}q_{n}-q_{n}^{2})+p^{n-5}[n]_{p_{n},q_{n}}(-p_{n}^{3}+3p_{n}q_{n}^{2}+
q_{n}^{3})-p_{n}^{3n-6}(p_{n}^{2}+p_{n}^{3}+2p_{n}q_{n}^{2}+q_{n}^{3})}{[n]^{3}_{p_{n},q_{n}}}\\
A_{2,n}&=&\frac{p_{n}^{n-3}[n]^{2}_{p_{n},q_{n}}(p_{n}^{2}-2p_{n}q_{n}+q_{n}^{2})}{[n]^{3}_{p_{n},q_{n}}}\\
&&+\frac{p^{2n-5}[n]_{p_{n},q_{n}}(-q_{n}^{3}-4p_{n}q_{n}^{2}-3p^{2}_{n}q_{n}+2p_{n}^{3})-p_{n}^{3n-6}(3p_{n}^{3}+3p_{n}q_{n}^{2}+5p^{2}_{n}q_{n}+q_{n}^{3})}{[n]^{3}_{p_{n},q_{n}}}\\
A_{3,n}&=&\frac{p_{n}^{2n-4}[n]_{p_{n},q_{n}}(-p_{n}^{2}+3p_{n}q_{n}+q_{n}^{2})-p_{n}^{3n-5}(3p_{n}^{2}+q_{n}^{2}+3p_{n}q_{n})}{[n]^{3}_{q_{n}}}\\
A_{4,n}&=&\frac{p^{3n-3}}{[n]^{3}_{q_{n}}},\\
\end{eqnarray*}

It is clear that
\begin{eqnarray}\label{ali1}
\lim_{n \to \infty} [n]_{q_{n}}^{2}\{A_{4,n}x\}=0.
\end{eqnarray}

Taking the limit of both sides of $A_{1,n}$, we get
\begin{eqnarray}\label{ali2}
\lim_{n\to\infty}[n]_{q_{n}}^{2}\{A_{1,n}\}&=&\lim_{n\to\infty}\left\{-p_{n}^{n-3}[n]_{p_{n},q_{n}}(p_{n}-q_{n})^{2}
+p_{n}^{n-5}(-p_{n}^{3}+3p_{n}q_{n}^{2}+q_{n}^{3})-\frac{p_{n}^{3n-6}(p_{n}^{2}+p_{n}^{3}+2p_{n}q_{n}^{2}+q_{n}^{3})}{[n]_{q_{n}}}\right\}\nonumber\\
&=&\lim_{n \to
\infty}\left\{-p_{n}^{n-3}(p^{n}_{n}-q^{n}_{n})(p_{n}-q_{n})+p_{n}^{n-5}(-p_{n}^{3}+3p_{n}q_{n}^{2}+
q_{n}^{3})-\frac{p_{n}^{3n-6}(p_{n}^{2}+p_{n}^{3}+2p_{n}q_{n}^{2}+q_{n}^{3})}{[n]_{q_{n}}}\right\}\nonumber\\
&=&3a.
\end{eqnarray}
Similarly, we can show that;

\begin{eqnarray}\label{ali4}
\lim_{n\to\infty}[n]_{q_{n}}^{2}\{A_{2,n}\}=-6a \,\,\textrm{and}\,\,
\lim_{n\to\infty}[n]_{q_{n}}^{2}\{A_{3,n}\}=3a.
\end{eqnarray}


By combining (\ref{ali1})-(\ref{ali4}), we reach the desired the
result.
\end{proof}

Now, we give a Voronovskaja type theorem for
$B^{(p_{n},q_{n})}_{n,n}(f;x,y)$.

\begin{thm}
Let $f\in C^{2}([0,1]\times[0,1])$. Then, we have
\begin{eqnarray*}
\lim_{n\to\infty}[n]_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}(f;x,y)-f(x_,y))&=&\frac{(ax-ax^{2})f_{x^{2}}^{\prime\prime}(x,y)}{2}+\frac{(ay-ay^{2})f_{y^{2}}^{\prime\prime}(x,y)}{2}.
\end{eqnarray*}
\end{thm}

\begin{proof}
Let $(x, y)\in [0, 1]\times[0, 1]$. Then, write Taylor's expansion
of $f$ as follows:
\begin{eqnarray}\label{taylor}
f(s,t)&=&f(x,y)+f_{x}^{\prime}(s-x)+f^{\prime}_{y}(t-y)\nonumber\\
&&+\frac{1}{2}\left\{f_{x}^{\prime\prime}(t-x)^{2}+2f_{xy}^{\prime}(s-x)(t-y)+f^{\prime\prime}_{y}(t-y)^{2}\right\}+\varepsilon(s,t)\left((s-x)^{2}+(t-y)^{2}\right)
\end{eqnarray}
where $(s,t)\in [0,1]^{2}$  and $\varepsilon(s,t)\longrightarrow 0$
as $(s,t) \longrightarrow (x,y)$.

Applying  the operator $B^{(p_{n},q_{n})}_{n,n}(f;.)$ on
\eqref{taylor}, we get
\begin{eqnarray*}
B^{(p_{n},q_{n})}_{n,n}(f;s,t)-f(x_,y)&=&f_{x}^{\prime}(x,y)B^{(p_{n},q_{n})}_{n,n}((s-x);x,y)+f_{y}^{\prime}(x,y)B^{(p_{n},q_{n})}_{n,n}((t-y);x,y)\\
&&+\frac{1}{2}\bigg\{f_{x^{2}}^{\prime\prime}B^{(p_{n},q_{n})}_{n,n}((t-x_{0})^{2};x,y)+
2f_{xy}^{\prime}B^{(p_{n},q_{n})}_{n,n}((s-x)(t-y);x,y)\\&&+f^{\prime\prime}_{y^{2}}B^{(p_{n},q_{n})}_{n,n}((t-y)^{2};x,y)\bigg\}
+B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon(s,t)\left((s-x)^{2}+(t-y)^{2}\right);x,y\right).
\end{eqnarray*}
Let us take the limit of both sides of the above equality as
$n\longrightarrow \infty$,
\end{proof}
\begin{eqnarray*}
\lim_{n \to
\infty}[n]_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}(f;s,t)-f(x,y))&=&\lim_{n
\to\infty}[n]_{p_{n},q_{n}}\frac{1}{2}\bigg\{f_{x^{2}}^{\prime\prime}B^{(p_{n},q_{n})}_{n,n}((t-x)^{2};x,y)\\&&+
2f_{xy}^{\prime}B^{(p_{n},q_{n})}_{n,n}((s-x)(t-y);x,y)+f^{\prime\prime}_{y^{2}}B^{(p_{n},q_{n})}_{n,n}((t-y)^{2};x,y)\bigg\}
\\&&+\lim_{n \to\infty}[n]_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon(s,t)\left((s-x)^{2}+(t-y)^{2}\right);x,y\right).
\end{eqnarray*}
For the last term on the right hand side, using Cauchy-Schwartz
inequality, we get
\begin{eqnarray*}
B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon(s,t)\left((s-x)^{2}+(t-y)^{2}\right);x,y\right)&{\leqslant}&
\sqrt{\lim_{n \to
\infty}B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon^{2}(s,t);x,y\right)}\\
&\times&\sqrt{2\lim_{n
\to\infty}[n]^{2}_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon(s,t)\left((s-x)^{4}+(t-y)^{4}\right);x,y\right)}.
\end{eqnarray*}
As $\lim_{n \to
\infty}B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon^{2}(s,t);x,y\right)=\varepsilon^{2}(x,y)=0$
and using Lemma \ref{lemmaVAR}(ii)

$\lim_{n\to\infty}[n]^{2}_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}\left((s-x)^{4}+(t-y)^{4});x,y\right)$
is finite, then we obtain
\begin{eqnarray*}
\lim_{n
\to\infty}[n]^{2}_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}\left(\varepsilon(s,t)\left((s-x)^{4}+(t-y)^{4}\right);x,y\right)=0.
\end{eqnarray*}
Hence, one can see that

\begin{eqnarray*}
\lim_{n\to\infty}[n]_{p_{n},q_{n}}B^{(p_{n},q_{n})}_{n,n}(f;x,y)-f(x_,y))&=&\frac{(ax-ax^{2})f_{x^{2}}^{\prime\prime}(x,y)}{2}+\frac{(ay-ay^{2})f_{y^{2}}^{\prime\prime}(x,y)}{2}.
\end{eqnarray*}
This step completes the proof.



\end{proof}


