\textbf{Lemma 2.} \textit{The uplink and downlink association probability to a sub-6GHz Mcell or mmWave Scell are given below.}
\begin{eqnarray}
\begin{aligned}
\mathcal{A}_{c, m} &= \frac{2 \pi \lambda_m}{\alpha_m a_c^{\frac{2}{\alpha_m}}} \int\limits_0^{\infty} l^{\frac{2}{\alpha_m }-1} \exp\Bigg( - \pi \lambda_m \left(\frac{l}{a_c}\right)^{\frac{2}{\alpha_m}}\Bigg)\Bigg( \exp\left(-\pi\lambda_s (\mathrm{\omega} l^{\frac{2}{\alpha_l}} + (1-\mathrm{\omega})l^{\frac{2}{\alpha_n}} ) \right)\mathds{1}(l < \mathrm{\mu}^{\alpha_l}) \\ & +  \exp\left( -\pi \lambda_s \left((1-\mathrm{\omega}) l^{\frac{2}{\alpha_n}} +\mathrm{\omega} \mathrm{\mu}^2\right) \right)\mathds{1}( \mathrm{\mu}^{\alpha_l} \leq l \leq \mathrm{\mu}^{\alpha_n}) + \exp\left( -\pi\lambda_s  l^{\frac{2}{\alpha_n}}\right)\mathds{1} (l > \mathrm{\mu}^{\alpha_n}) \Bigg) \mathrm{d}l \label{A_M_DL}\\
\mathcal{A}_{c, s} &= 1 - \mathcal{A}_{c, m},
\end{aligned}
\end{eqnarray}


where  $c \in \{\mathrm{UL}, \mathrm{DL}\}$, $ a_{\mathrm{DL}} = \frac{\mathrm{P}_{s} \mathrm{T}_{s} \psi_s}{\mathrm{P}_{m} \mathrm{T}_{m} \psi_m}$ and $a_{\mathrm{UL}} = \frac{\mathrm{P}_{us} \mathrm{T}_{s}' \psi_s}{\mathrm{P}_{um} \mathrm{T}_{m}' \psi_m}$. \\

\begin{proof}[Proof:\nopunct]
The proof for $\mathcal{A}_{\mathrm{DL}, m}$ is given below.
\begin{eqnarray*}
\mathcal{A}_{\mathrm{DL}, m} &=& \mathbb{P} \left( \mathrm{P}_{um} \mathrm{T}_{m} \psi_m L_{min,m}^{-1} > \mathrm{P}_{us} \mathrm{T}_{s} \psi_s L_{min,s}^{-1} \right) = \mathbb{P} \left(L_{min,s} > a_{\mathrm{DL}} L_{min,m} \right)  \\
&=&  \int \limits_0^{\infty}  \bar{F}_{s}(a_{\mathrm{DL}} l_m) f_{m}(l_m) \mathrm{d}l_m,
\end{eqnarray*}

where the last step follows from the fact that  $\mathbb{P} (X > Y) = \int\limits_0^{\infty} \mathbb{P} (X>y) f_Y(y) \mathrm{d}y $.

Changing variables as  $ l = a_{\mathrm{DL}} l_m $ yields
\begin{eqnarray*}
\mathcal{A}_{\mathrm{DL}, m}  = \frac{1}{a_{\mathrm{DL}}} \int\limits_0^{\infty}  \bar{F}_{s}(l) f_{m}\left(\frac{l}{a_{\mathrm{DL}}} \right) \mathrm{d}l.
\end{eqnarray*}
This directly results in $\mathcal{A}_{\mathrm{DL}, m}$, and $\mathcal{A}_{\mathrm{UL}, m}$ follows similarly.
\end{proof}
\textbf{Corollary 1.} \textit{The association probabilities can be acquired in closed form for the special case where $\alpha_l = 2$ and $\alpha_n = \alpha_m = 4$ and with simple mathematical manipulation, $\mathcal{A}_{\mathrm{DL}, m}$ can be expressed by}
\begin{eqnarray}
\begin{aligned}
\mathcal{A}_{ \mathrm{DL},m} = \frac{\pi \lambda_m}{\sqrt{a_{\mathrm{DL}}}} \Bigg( \frac{\sqrt{\pi} e^{\frac{c_2^2}{4 c_1}}}{2\sqrt{c_1}}\left(Q\left(\frac{c_2}{\sqrt{2 c_1}}\right) - Q \left(\frac{2 \mathrm{\mu} c_1 + c_2}{\sqrt{2 c_1}}\right)\right) + e^{-\mathrm{\mu}^2 c_1} \left( \frac{e^{-\mathrm{\mu} c_2}}{c_2} - \frac{c_1 e^{-\mathrm{\mu}^2 c_2}}{c_2(c_1 + c_2)} \right)\Bigg),
\end{aligned}
\end{eqnarray}
where $c_1 = \pi \lambda_s \mathrm{\omega} $, $c_2 = \pi \lambda_s (1 - \mathrm{\omega}) + \frac{\pi \lambda_m}{a_{\mathrm{DL}}^{2/\alpha_n}} $ and $Q(.)$ is the Q-function.
Similarly, the other three cases can be obtained in closed form.


