\section{}
\label{Theorem1_proof}
\begin{proof}[\unskip\nopunct]
\textit{Derivation of Theorem 1:}
The DL $\mathtt{SINR}$ coverage for sub-6GHz Mcells is first derived. As shown in Section \ref{Max_RP}, the condition for association to a Mcell in the DL is $L_{\min, s} > a_{DL} L_{\min, m}$.
\begin{eqnarray*}
\begin{aligned}
\mathcal{P}_{DL , m} (\tau)  &=  \mathbb{P} (\mathtt{SINR}_{\mathrm{DL} , m} > \tau ; K_{\mathrm{DL}} = m) = \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , m} > \tau; K_{\mathrm{DL}} = m | L_{\min, m} =l) \ f_{m}(l) \mathrm{d}l \\
&=    \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , m} > \tau; L_{\min, s} > a_{\mathrm{DL}} l| L_{\min, m} =l) \ f_{m}(l) \mathrm{d}l \\
&\stackrel{(a)}{=} \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , m} > \tau| L_{\min, m} =l) \  \mathbb{P}( L_s > a_{\mathrm{DL}} l| L_{\min, m} = l) \ f_{m}(l) \mathrm{d}l \\
&= \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , m} > \tau| L_{\min, m} =l) \bar{F}_{s} (a_{\mathrm{DL}} l) f_{m}(l) \mathrm{d}l,
\end{aligned}
\end{eqnarray*}
where (a) follows from the assumption that $\Phi_s$ and $\Phi_m$ are independent.
Since $\bar{F}_{s} $ and $f_{m}$ are known, we now derive the first part of the equation which is given by
\begin{eqnarray*}
\begin{aligned}
\mathbb{P} (\mathtt{SINR}_{\mathrm{DL},m}  > \tau| L_{\min, m} =l)  &= \mathbb{P} \left(\frac{ \mathrm{P}_{m}  \psi_m h_{x^*, 0} l^{-1}}{ I + \sigma_m^2}> \tau | L_{\min, m} = l  \right)\\
& = \exp\left(\frac{-\tau \sigma_m^2 l}{\mathrm{P}_{m}  \psi_m} \right)  \mathbb{E}_I \left[ \exp\left( \frac{-\tau I l}{\mathrm{P}_{m}  \psi_m}  \right) \right]
 =  \exp\left(\frac{-\tau \sigma_m^2 l}{\mathrm{P}_{m}  \psi_m} \right) \mathcal{L}_{I_l} \left(  \frac{-\tau l}{\mathrm{P}_{m}  \psi_m} \right),
\end{aligned}
\end{eqnarray*}
where $\mathcal{L}_I(s)$ is the Laplace transform of the interference and is given by
\begin{eqnarray*}
\begin{aligned}
\mathcal{L}_{I_l}(s)  &= \mathbb{E} [e^{-sI} ] =  \mathbb{E}_{\Phi, h_{x, 0}} \left[ \exp\left(-s\sum\limits_{{{x}} \in {\Phi_{m}}\backslash x^*}\mathrm{P}_{m}  \psi_m h_{x, 0} L_{m}(x)^{-1} \right) \right] \\
&= \mathbb{E}_{\Phi, h_{x, 0}} \left[ \prod\limits_{{{x}} \in {\Phi_{m}}\backslash x^*} \exp\left( -s \mathrm{P}_{m}  \psi_m h_{x, 0} L_{m}(x)^{-1}  \right)\right]\\
&= \exp\left(- \int\limits_l^{\infty} \left(1- \mathbb{E}_{h_{x, 0}} \left[\exp\left( -s \mathrm{P}_{m}  \psi_m h_{x, 0} t^{-1}\right)\right]\right) \Lambda_m(\mathrm{d}t)\right),
\end{aligned}
\end{eqnarray*}
where $\Lambda_m(\mathrm{d}t) $ is given by deriving the expression in (\ref{lambda_m}) with respect to t.
\begin{eqnarray*}
\begin{aligned}
\mathcal{L}_{I_l}(s ) = \exp\left( \frac{-2 \pi \lambda_m}{\alpha_m} \int\limits_l^{\infty} \frac{t^{\frac{2}{\alpha_m}-1}}{1+ \frac{t}{s \mathrm{P}_{m}  \psi_m }} \mathrm{d}t \right).
\end{aligned}
\end{eqnarray*}
Finally,
\begin{eqnarray*}
\begin{aligned}
\mathcal{P}_{\mathrm{DL} , m} (\tau)&= \int \limits_0^{\infty} \exp\left( \frac{-\tau \sigma_m^2 l}{\mathrm{P}_{m}  \psi_m} \right) \exp\left( \frac{-2\pi \lambda_m}{\alpha_m} \int \limits_l^{\infty} \frac{t^{\frac{2}{\alpha_m}-1}}{1 + \frac{t}{\tau l}} dt \right) \bar{F}_s (a_{\mathrm{DL}} l) f_m(l) \mathrm{d}l.
\end{aligned}
\end{eqnarray*}
$\mathcal{P}_{\mathrm{UL} , m}$ is derived in the same way replacing $ \mathrm{P}_{m}$ and $ a_{\mathrm{DL}}$ by $ \mathrm{P}_{um}$ and $ a_{\mathrm{UL}}$ respectively.








We then derive the DL $\mathtt{SNR}$ coverage for mmWave Scells. Similarly, the condition for association to a Scell in the DL is $L_{\min, m} > \frac{L_{\min, s}}{a_{\mathrm{DL}}} $.
\begin{eqnarray*}
\begin{aligned}
\mathcal{P}_{\mathrm{DL} , s} (\tau)  &=  \mathbb{P} (\mathtt{SINR}_{\mathrm{DL} , s} > \tau ; K_{\mathrm{DL}} = s) = \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , s} > \tau; K_{\mathrm{DL}} = s | L_{\min, s} =l) \ f_{s}(l) \mathrm{d}l \\
&= \int\limits_0^{\infty} \mathbb{P}(\mathtt{SINR}_{\mathrm{DL} , s} > \tau| L_{\min, s} =l) \bar{F}_{m} \left(\frac{l}{a_{\mathrm{DL}}}\right) f_{s}(l) \mathrm{d}l.
\end{aligned}
\end{eqnarray*}
\begin{eqnarray*}
\begin{aligned}
\mathbb{P} (\mathtt{SNR}_{\mathrm{DL},s}  > \tau| L_{\min, s} =l)  &= \mathbb{P} \left(\frac{ \mathrm{P_{s}}  \psi_s h_{x^*, 0}  l^{-1}}{ \sigma_s^2}> \tau | L_{\min, s} = l  \right)\\
& = \mathbb{P} \left( h_{x^*, 0} > \frac{-\tau \sigma_s^2 l}{\mathrm{P_{s}}  \psi_s  }  | L_{\min, s} = l  \right)
 = \exp\left( \frac{-\tau \sigma_s^2 l}{\mathrm{P_{s}}  \psi_s} \right).
\end{aligned}
\end{eqnarray*}
Finally,
\begin{eqnarray*}
\begin{aligned}
\mathcal{P}_{\mathrm{DL} , s} (\tau)&= \int \limits_0^{\infty}\exp\left( \frac{-\tau \sigma_s^2 l}{\mathrm{P_{s}}  \psi_s} \right) \bar{F}_m \left(\frac{l}{a_{\mathrm{DL}}}\right) f_s(l) \mathrm{d}l.
\end{aligned}
\end{eqnarray*}
$\mathcal{P}_{\mathrm{UL} , s} (\tau)$ is derived similarly by exchanging $\mathrm{P}_s$ and $a_{\mathrm{DL}}$ by $\mathrm{P}_{us}$ and $a_{\mathrm{UL}}$ respectively in the previous derivation.




\end{proof}
