\begin{algorithm}
\caption{$\mathtt{FillMatrix}$}
\begin{algorithmic}[1]
  \Require $\mathtt{case} = (d,m)$, a pair from Equation~\eqref{cases}; 
$\mathtt{basis}$, a subset of $\{1, \dotsc, m\}$ of cardinality~$d$.
  \Ensure a matrix $G$, filled with entries belonging to $\{-1,0,1\}$ and 
ensuring Conditions (C2) and (C3) from Definition~\ref{reducedrealizationspace}.
  \Statex
    \State {\bfseries Create} a $d \times m$ matrix~$G$, and fill it with $-1$ 
entries.
    \State {\bfseries Set} $\mathtt{non\_basis}$ to be equal to the set $\{1, 
\dotsc, m\} \setminus \mathtt{basis}$.
    \State \Comment{Imposing Condition (C2).}
    \State {\bfseries Insert} in $G$ a $d \times d$ identity matrix in 
correspondence to the columns of $\mathtt{basis}$.
    \State \Comment {Inserting as many zeroes as possible in~$G$.}
    \For {$j$ in $\mathtt{non\_basis}$}
      \For {$i \in \{1, \dotsc, d\}$}
        \If {$\bigl( \{1, \dotsc, d\} \setminus \{i\} \bigr) \cup \{j\}$ is 
not a basis of $\mathtt{matroid}$} 
          \State {\bfseries Set} $G(i,j) = 0$.
        \EndIf
      \EndFor
    \EndFor
    \State \Comment {Computing the normal frame and imposing Condition 
(C3).}
    \State \Comment {Inserting $1$s column by column.}
    \For {$j$ in $\mathtt{non\_basis}$}
      \State {\bfseries Set} $r = 1$.
      \While {$G(r,j) = 0$}
        \State {\bfseries Increase} $r$ by~$1$.
        \If {$r = d+1$} {\bfseries Break} the loop. 
        \EndIf
      \EndWhile
      \If {$r {\leqslant} d$} {\bfseries Set} $G(r,j) = 1$.
      \EndIf
    \EndFor
    \State \Comment {Inserting $1$s row by row.}
    \For {$i \in \{1, \dotsc, d\}$}
      \State {\bfseries Set} $c = 1$.
      \While {$G(i,c) = 0$ or $G(i,c) = 1$}
        \State {\bfseries Increase} $c$ by~$1$.
        \If {$c = m+1$} {\bfseries Break} the loop. 
        \EndIf
      \EndWhile
      \If {$c {\leqslant} m$} {\bfseries Set} $G(i,c) = 1$.
      \EndIf
    \EndFor
  \State \Return $G$.
\end{algorithmic}
\end{algorithm}




