\title{Resonance spectrum of a bulk fermion on branes}


\author{Yu-Peng Zhang\footnote{zhangyupeng14@lzu.edu.cn},
        Yun-Zhi Du\footnote{duyzh13@lzu.edu.cn},
        Wen-Di Guo\footnote{guowd14@lzu.edu.cn},
        Yu-Xiao Liu \footnote{liuyx@lzu.edu.cn, corresponding author},
