h(T_D)&=N \int_{T_D}^{T_f} S(t) dt \nonumber \\ &= N\frac{1-I_0}{I_0} \ln \frac{I_0 \exp \{ -\lambda T_D\}+1-I_0} {I_0 \exp\{-\lambda T_f\}+1-I_0}, \\
f_0(T_D)&=N\int_{T_D}^{T_f} \frac{N-1}{N}\exp\{-\frac{\lambda}{N}(t-T_D)\} dt\\ &=N(N-1)[ 1-\exp\{-\frac{\lambda}{N}(T_f-T_D)\} ] \\
f_{\kappa}(T_D)&=N \int_{T_D}^{T_f} R(t)dt \nonumber \\ &=\frac{N}{\kappa} \ln \frac{\exp \{\lambda \kappa (T_f-T_D)\}+N-1}{N}.
\end{aligned}
\right.
\end{IEEEeqnarray}

Since $T_f \geq T_D$, the buffer occupancy becomes

text:
\begin{align}
      \label{eqn_buffer_early}
          B&=N \left(  \int_{0}^{T_D} I(t) dt+\int_{T_D}^{T_f} \left[1-R(t)-S(t)\right] dt \right) \\
          &=\left\{
          \begin{array}{ll}
              g(T_D)-h(T_D)+f_0(T_D),&\kappa=0,  \\
              g(T_D)-h(T_D)+N(T_f-T_D)-f_{\kappa}(T_D), &\kappa \in (0,1],\nonumber
          \end{array}
          \right.
      \end{align}
