\item $\psi(s)=\mathcal{O}(\varepsilon^{-1}),\ \psi'(s)=1\ for |s|\leq \varepsilon/4\ and psi(s)=psi'(s)=0,\ |s|>(3/4)\varepsilon$
\end{enumerate} 

