\label{Eq:Lag1}
\end{eqnarray}
where $A\buildrel\leftrightarrow\over\partial{}B\equiv A (\partial B)-(\partial A) B$, $\vec\tau$ is the Pauli matrix and $\vec\pi$ is the pion triplet, while $K^{(\ast)}$ and $D^{(\ast)}$ are the doublets of strange and charmed mesons, respectively, which are,

text:
\begin{equation}
K^{(\ast )}=\left(\begin{array}{c}
K^{(\ast )+}\\
K^{(\ast )0}
\end{array}\right)\,,\qquad
D^{(\ast )}=\left(\begin{array}{c}
D^{(\ast )0}\\
D^{(\ast )+}
\end{array}\right)\,,
\end{equation}

nexttext:
where $m_u$ and $m_d$ are the $u$ and $d$ current quark masses, respectively, $\mu$ is the condensate parameter.

\subsection{The decay of $D_{s1}(2460)\to D_{s0}^\ast(2317) \pi^0$}

\begin{figure}[htb]
\begin{tabular}{cc}
 \includegraphics[scale=0.38]{tri-ds0pi1.eps}
&\includegraphics[scale=0.38]{tri-ds0pi2.eps}\\
(a) &(b)
\end{tabular}
\caption{The diagrams contributing to the pionic transition from $D_{s1}(2460)$ to $D_{s0}^\ast(2317)$, where diagram (a) is the direct contribution and diagram (b) is the contribution from $\eta-\pi^0$ mixing \label{fig:feyn-pi-a}}
\end{figure}

The decay of $D_{s1}(2460)\to D_{s0}^\ast(2317) \pi^0$ occurs via a subprocess $D^\ast \to D \pi^0$ in the hadronic molecule picture, and the hadronic level description of this process is present in Fig. \ref{fig:feyn-pi-a}(a). Since this decay is an isospin violating process, we also include the contributions from $\eta-\pi^0$ mixing as present in Fig. \ref{fig:feyn-pi-a}(b). With the effective interaction list above,we can get the amplitude corresponding to Fig. \ref{fig:feyn-pi-a}(a), which is,
\begin{eqnarray}
\mathcal{M}_{a}&=& (i)^3 \int \frac{d^4q}{(2\pi)^4} \big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}}\tilde\Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big] \nonumber\\
&&\times \Big[g_{D^\ast _{s0}DK}\tilde\Phi_{D_{s0}^\ast }(-P_{20}^2,\Lambda^2_{D_{s0}^\ast })\Big] \ \Big[\frac{ig_{D^\ast DP}}{\sqrt{2}}(-ip_3^{\mu})\Big]  \nonumber\\
&&\times  \frac{-g^{\phi\mu}+p_1^{\phi}p_1^{\mu}/m_1^2}{p_1^2-m_1^2}
    \frac{1}{p_2^2-m_2^2}
    \frac{1}{q^2-m_q^2},\label{Eq:pi-a}
\end{eqnarray}
where $P_{12}=(p_1w_{D^\ast K}-p_2w_{KD^\ast })$ and $P_{20}=qw_{DK}-p_2w_{KD}$. The amplitude related to diagram (b) in Fig. \ref{fig:feyn-pi-a} is,
\begin{eqnarray}
\mathcal{M}_b&=&(i)^3 \int \frac{d^4q}{(2\pi)^4} \big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}}\tilde\Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big] \nonumber\\
&&\times \Big[g_{D^\ast _{s0}DK}\tilde\Phi_{D_{s0}^\ast }(-P_{20}^2,\Lambda^2_{D_{s0}^\ast })\Big] \ \Big[\frac{ig_{D^\ast D\eta}}{\sqrt{2}}(-ip_3^{\mu})\Big]  \nonumber\\
&&\times  \frac{-g^{\phi\mu}+p_1^{\phi}p_1^{\mu}/m_1^2}{p_1^2-m_1^2}
    \frac{1}{p_2^2-m_2^2}
    \frac{1}{q^2-m_q^2}\nonumber\\
&&\times\mu\frac{m_d-m_u}{\sqrt{3}}\frac{1}{m_{\pi}^2-m_{\eta}^2},\label{Eq:pi-b}
\end{eqnarray}
where $m_{\pi}^2=(m_u+m_d)\mu$, $m_{\eta}^2=\frac{2}{3}(m+2m_s)\mu$ and $m=(m_u+m_d)/2$. The above amplitude $\mathcal{M}_b$ can be reduced to
\begin{eqnarray}
&&\mathcal{M}_b=\mathcal{M}_a|_{\pi^0 \to \eta}\frac{\sqrt{3}}{4}\frac{(m_d-m_u)}{(m_s-m)},
\end{eqnarray}
where $\mathcal{M}_a|_{\pi^0 \to \eta}$ indicates the amplitude obtained by replacing the related coupling constants of $\pi^0$ with those of $\eta$. The total amplitudes of $D_{s1}(2460) \to D_{s0}^\ast(2317) \pi^0$ is,
\begin{eqnarray}
\mathcal{M}_{D_{s1}\to D_{s0}^\ast \pi^0} =\mathcal{M}_a +\mathcal{M}_b. \label{eq:amp1}
\end{eqnarray}




\subsection{The decay of $D_{s1}(2460) \to D_{s0}^\ast(2317) \gamma$}

\begin{figure}[htb]
\begin{tabular}{cc}
\includegraphics[scale=0.38]{tri-ds0gamma2.eps}
&\includegraphics[scale=0.38]{tri-ds0gamma1.eps}\\
(a) &(b)
\end{tabular}
\caption{Diagrams contributing to the radiative transition form $D_{s1}(2460)$ to $D_{s0}^\ast(2317)$. \label{fig:feyn-gamma}}
\end{figure}

As for the decay of $D_{s1}(2460) \to D_{s0}^\ast(2317) \gamma$, it occurs via the subprocess $D^\ast \to D \gamma $ as shown in Fig. \ref{fig:feyn-gamma}. With the effective Lagrangians listed above, we can obtain the amplitude corresponding to the Fig. \ref{fig:feyn-gamma}(a), which is,
\begin{eqnarray}
\mathcal{M}_{a}  &=&
   (i)^3 \int \frac{d^4q}{(2\pi)^4}\Big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}}\tilde\Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big] \nonumber\\
   && \times  \Big[g_{D^\ast _{s0}DK}\tilde\Phi_{D_{s0}^\ast }(-P_{20}^2,\Lambda^2_{D_{s0}^\ast })\Big]\Big[\frac{e g_{D^{\ast +}D^+\gamma}}{4} \nonumber\\
&& \times
\epsilon_{\mu\nu\alpha\beta}\epsilon^{\eta}_{\gamma}(ip_3^{\nu}g^{\mu\eta}-ip_3^{\nu}g^{\nu\eta})
    (ip_1^{\alpha}g^{\beta\tau}-ip_1^{\beta}g^{\alpha\tau})\Big]\nonumber\\
&&\times
    \frac{-g^{\phi\tau}+p_1^{\phi}p_1^{\tau}/m_1^2}{p_1^2-m_1^2}
    \frac{1}{p_2^2-m_2^2}
    \frac{1}{q^2-m_q^2}.
\end{eqnarray}
As for the amplitude corresponding to Fig. \ref{fig:feyn-gamma}(b), it can be obtained from above amplitude by replacing the masses and coupling constants with those in Fig. \ref{fig:feyn-gamma}(b), i.e.,
\begin{eqnarray}
\mathcal{M}_{b} = \mathcal{M}_{a}\left|_{g_{D^{\ast +} D^+ \gamma} \to g_{D^{\ast 0} D^0 \gamma} }^{m_{D^{\ast +}} \to m_{D^{\ast 0}}, m_{D^+} \to m_{D^0}, m_{K^0} \to m_{K^+}}\right.,
\end{eqnarray}
then the total amplitude for $D_{s1}(2460) \to D_{s0}^\ast(2317) \gamma$ is,
\begin{eqnarray}
\mathcal{M}_{D_{s1} \to D_{s0}^\ast \gamma} =\mathcal{M}_{c}+\mathcal{M}_d. \label{eq:amp2}
\end{eqnarray}
It should be notice that after performing the loop integral, the above amplitude can be reduce to the form,
\begin{eqnarray}
 \mathcal{M}_{D_{s1} \to D_{s0}^\ast \gamma}= g_{D_{s1} D_{s0}^\ast \gamma} \varepsilon_{\mu \nu \alpha \beta} \epsilon_{D_{s1}}^\mu \epsilon_\gamma^\nu p_\gamma^\alpha p_{D_{s1}}^\beta,
\end{eqnarray}
which is obviously gauge invariance for the photon field and the coupling constant $ g_{D_{s1} D_{s0}^\ast \gamma}$ could be estimated from the amplitude in Eq. (\ref{eq:amp2}).

\subsection{The decay of $D_{s1}(2460) \to D_s^\ast \pi^0$}
\begin{figure}[h]
\begin{tabular}{ccc}
\includegraphics[scale=0.35]{tri-dsstarpia.eps}
&\includegraphics[scale=0.35]{tri-dsstarpic.eps}
&\includegraphics[scale=0.35]{tri-dsstarpie.eps}\\
(a) & (b) & (c)\\
\includegraphics[scale=0.35]{tri-dsstarpib.eps}
&\includegraphics[scale=0.35]{tri-dsstarpid.eps}
&\includegraphics[scale=0.35]{tri-dsstarpif.eps}\\
(d) & (e) & (f)
\end{tabular}
\caption{Diagrams contributing to process $D_{s1}^+\to D_s^{\ast +}\,\pi^0$. Diagrams (a), (b) and (c) indicate direct processes, where the $\pi^0$ directly couples to strange mesons or charmed mesons. Diagrams (d), (e) and (f) are indirect process, where $\pi^0$ couples to  strange mesons or charmed mesons via $\eta-\pi^0$ mixing.\label{fig:feyn-sstar} }
\end{figure}

Here we can estimate the partial width of $D_{s1}(2460) \to D_{s}^\ast \pi^0$ and compare the evaluated ratio of the $\Gamma(D_{s1}(2460) \to D_{s0}^\ast \gamma)$ and $\Gamma(D_{s1}(2460) \to D_{s}^\ast \pi^0)$ to further test the hadronic molecule interpretations to the $D_{s1}(2460)$ and $D_{s0}^\ast(2317)$. Similar to the process $D_{s1}(2460) \to D_{s0}^\ast \pi^0$, the decay $D_{s1}(2460) \to D_s^\ast \pi^0$ is also an isospin violating process, which have contributions from the direct $\pi^0$ coupling and $\eta-\pi^0$ mixing as shown in Fig.~\ref{fig:feyn-sstar}. In our calculation, in addition to the diagrams considered in Ref.~\cite{Faessler:2007us}, we include the diagrams due to the $D^\ast D^\ast \pi$ and $D^\ast D^\ast \eta$ interactions. The concrete forms of the amplitudes corresponding to Figs. \ref{fig:feyn-sstar}(a)-\ref{fig:feyn-sstar}(c) are,

\begin{eqnarray}
{\mathcal{M}_a} &=&
    (i)^3\int \frac{d^4q}{(2\pi)^4}\Big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}}\tilde \Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big] \nonumber\\
  &&\times \Big[ig_{K^\ast K\pi}(ip_1^{\eta}+ip_3^{\eta})\Big] \Big[ig_{D^\ast D^\ast V}\epsilon^{\tau}_{D_s^\ast }g^{\tau\rho}
    (ip_2^{\sigma}+ip_4^{\sigma})\nonumber\\
&&+4if_{D^\ast D^\ast V}\epsilon^{\tau}_{D_s^\ast }(iq^{\tau}g^{\rho\sigma}-iq^{\rho}g^{\tau\sigma})\Big]
    \frac{1}{p_1^2-m_1^2}\nonumber\\
&& \times
    \frac{-g^{\rho\phi}+p_2^{\rho}p_2^{\phi}/m_2^2}{p_2^2-m_2^2}
    \frac{-g^{\eta\sigma}+q^{\eta}q^{\sigma}/m_q^2}{q^2-m_q^2}\,,\\




{\mathcal{M}_b} &=&
    (i)^3\int \frac{d^4q}{(2\pi)^4}\Big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}} \tilde\Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big] \nonumber\\
&&\times  \Big[\frac{-ig_{D^\ast DP}}{\sqrt{2}}(-ip_3^{\mu})\Big]
    \Big[ig_{D^\ast DP}\epsilon^{\nu}_{D_s^\ast }(ip_2^{\nu})\Big]\nonumber\\
&&\times\frac{-g^{\phi\mu}+p_1^{\mu}p_1^{\phi}/m_1^2}{p_1^2-m_1^2}
    \frac{1}{p_2^2-m_2^2}
    \frac{1}{q^2-m_q^2}\,,\\




{\mathcal{M}_c} &=&
    (i)^3\int \frac{d^4q}{(2\pi)^4}\Big[g_{D_{s1}D^\ast K}\epsilon^{\phi}_{D_{s1}} \tilde\Phi_{D_{s1}}(-P_{12}^2,\Lambda^2_{D_{s1}})\Big]\nonumber\\
  &&  \times\Big[\frac{1}{2\sqrt{2}}g_{D^\ast D^\ast P}\varepsilon_{\eta\tau\rho\sigma}(-ip_3^{\tau})(ip_1^{\rho}+iq^{\rho})\Big]\nonumber\\
&&\times
    \Big[\frac{1}{2}g_{D^\ast D^\ast P}\varepsilon_{\mu\nu\alpha\beta}\epsilon_{D_s^\ast }^{\mu}(ip_2^{\nu})(iq^{\alpha}+ip_4^{\alpha})\Big]
    \nonumber\\&&\times\frac{-g^{\sigma\phi}+p_1^{\sigma}p_1^{\phi}/m_1^2}{p_1^2-m_1^2}
     \frac{1}{p_2^2-m_2^2}
    \frac{-g^{\eta\beta}+q^{\eta}q^{\beta}/m_q^2}{q^2-m_q^2}\,.
\end{eqnarray}

As for the contributions from $\eta-\pi^0$ mixing, one can obtain the amplitudes corresponding to Figs. \ref{fig:feyn-sstar}(d)-\ref{fig:feyn-sstar}(f) by,
\begin{eqnarray}
\mathcal{M}_d&=&\mathcal{M}_a|_{\pi^0 \to \eta} \frac{\sqrt{3}}{4}\frac{(m_d-m_u)}{(m_s-m)},\nonumber\\
\mathcal{M}_e&=&\mathcal{M}_b|_{\pi^0 \to \eta} \frac{\sqrt{3}}{4}\frac{(m_d-m_u)}{(m_s-m)},\nonumber\\
\mathcal{M}_f&=&\mathcal{M}_c|_{\pi^0 \to \eta} \frac{\sqrt{3}}{4}\frac{(m_d-m_u)}{(m_s-m)}.
\end{eqnarray}
The total amplitude of $D_{s1}(2460) \to D_{s}^\ast \pi^0$ is,
\begin{eqnarray}
\mathcal{M}_{D_{s1}\to D_s^\ast \gamma}= \sum_{n=a}^{f} \mathcal{M}_n.
\label{eq:amp3}
\end{eqnarray}


With the total amplitudes defined in Eqs. (\ref{eq:amp1}), (\ref{eq:amp2}) and (\ref{eq:amp3}), one can estimate the partial width by,
\begin{eqnarray}
\Gamma=\frac{1}{3}\frac{1}{8\pi}\frac{|\vec p|}{m_{D_{s1}}^2}|\overline{\mathcal{M}}|^2,
\end{eqnarray}
where $\vec p$ is the momentum of final state in the $D_{s1}(2460)$ rest frame and the overline indicates sum over polarizations of vector mesons.

