\begin{eqnarray}
&&(q_1+q_2)_\mu[K_{\mu\rho\lambda}^{(1)111}(q_1,q_2)+K_{\mu\rho\lambda}^{(2)111}(q_1,q_2)]\nonumber\\
&=& (e^{-\frac{i}{2}(q_1-q_2)\cdot y}-e^{-\frac{i}{2}(q_1+q_2)\cdot y})\nonumber\\ &&\times\int\frac{d^4p}{(2\pi)^4}e^{-ip\cdot y}
{\rm tr}\gamma_\rho S_{11}(p)\gamma_{\lambda}\gamma_5 S_{11}(p+q_1)\nonumber\\
&+& (e^{\frac{i}{2}(q_1-q_2)\cdot y}-e^{-\frac{i}{2}(q_1+q_2)\cdot y})\nonumber\\ &&\times\int\frac{d^4p}{(2\pi)^4}e^{-ip\cdot y}
{\rm tr}\gamma_{\lambda}\gamma_5 S_{11}(p)\gamma_\rho S_{11}(p+q_2),
\end{eqnarray}
\begin{eqnarray}
&&(q_1+q_2)_\mu[K_{\mu\rho\lambda}^{(1)112}(q_1,q_2)+K_{\mu\rho\lambda}^{(2)112}(q_1,q_2)]\nonumber\\
&=& (e^{\frac{i}{2}(q_1-q_2)\cdot y}-e^{-\frac{i}{2}(q_1+q_2)\cdot y})\nonumber\\ &&\times\int\frac{d^4p}{(2\pi)^4}e^{-ip\cdot y}
{\rm tr}\gamma_\rho S_{12}(p+q_2)\gamma_{\lambda}\gamma_5 S_{21}(p),
\end{eqnarray}
\begin{eqnarray}
&&(q_1+q_2)_\mu[K_{\mu\rho\lambda}^{(1)121}(q_1,q_2)+K_{\mu\rho\lambda}^{(2)121}(q_1,q_2)]\nonumber\\
