\begin{equation} \label{PCJmatrix}
P(\mathcal{M}_i^{A},\mathcal{M}_j^{B}) = {\operatorname{Tr}} \left[ W ( M_i^{A_1 A_2} \otimes M_j^{B_1 B_2}) \right],
\end{equation}

\begin{equation}\label{Wstruct}
W = \frac{1}{d} \left( \mathds{1}^{A_1A_2 B_1 B_2} + \sum_{i} c_i W_i^{A_1A_2 B_1 B_2} \right).
\end{equation}

