\section{Introduction}

Minimally doubled fermions are a category of ultralocal, chiral lattice fermions that have only two degenerate flavours of quarks. Karsten-Wilczek fermions~\cite{kw} are a type of minimally doubled fermions that retains two original poles of the na\"{i}ve Dirac operator on the Euclidean time axis. Since the two poles of the Karsten-Wilczek operator lie on a line in the Brillouin zone, the hypercubic symmetry is inevitably broken and further anisotropic terms are generated dynamically. \newline
 The tree-level Karsten-Wilczek fermion action reads (with Wilczek parameter $ \zeta $, $ 4\zeta^2>1$)

text:
\begin{align}
   S^f_{+\zeta}[\psi,\bar{\psi},U] = a^4\sum\limits_{n\in\Lambda}&\ \sum\limits_{\mu=0}^3
   \bar{\psi}_n \frac{1}{2a} \gamma^\mu \left(U^\mu_n \psi_{n+\hat{\mu}} - U^{\mu\dagger}_{n-\hat{\mu}} \psi_{n-\hat{\mu}}\right) +m_0\bar{\psi}_n \psi_n \nonumber \\
    +& \sum\limits_{j=1}^3
   \bar{\psi}_n \frac{i\zeta}{2a} \gamma^0 \left(2\psi_n - U^j_n \psi_{n+\hat{j}} - U^{j\dagger}_{n-\hat{j}} \psi_{n-\hat{j}}\right) 
   \label{eq: KW tl f action}
 \end{align}

\begin{align}
   \psi_n \to e^{+i \alpha^V_n} \psi_n,\ \bar{\psi}_n \to \bar{\psi}_n e^{-i \alpha^V_n},& \qquad
   \psi_n \to e^{i \alpha^A_n \gamma^5} \psi_n,\ \bar{\psi}_n \to \bar{\psi}_n e^{+i \alpha^A_n \gamma^5}
   \label{eq: axial transform}.
 \end{align}

\begin{equation}
   \psi_n \to \widehat{T}\psi_n = T_n \psi_n, \quad \bar{\psi}_n \to (\widehat{T}\bar{\psi}_n) = \bar{\psi}_n T_n, \quad T_n \equiv i \gamma^0 \gamma^5 (-1)^{n_0},
   \label{eq: unitary transform T}
 \end{equation}

\begin{align}
   S^3_{+\zeta}[\psi,\bar{\psi}] =&\ \mathrm{sign}({\zeta})\, c(g_0^2)\ a^4\sum\limits_{n\in\Lambda} \bar{\psi}_n \frac{i}{a} \gamma^0 \psi_n 
   \label{eq: relevant term} \\
   S^{4f}[\psi,\bar{\psi},U] =&\ d(g_0^2)\ a^4\sum\limits_{n\in\Lambda} \bar{\psi}_n \frac{1}{2a} \gamma^0 \left(U^0_n \psi_{n+\hat{0}} - U^{0\dagger}_{n-\hat{0}} \psi_{n-\hat{0}}\right), 
   \label{eq: marginal fermion term} \\
   S^{4g}[U] =&\ d_p(g_0^2) \frac{\beta}{3} \sum\limits_{n\in\Lambda} \sum\limits_{j=1}^3 \mathrm{Re} \mathrm{Tr} (1-U^{j 0}_n),
   \label{eq: marginal gauge term}
 \end{align}

\begin{align}
  \mathcal{O}_{32}:\quad S^{5 t} =&\ c_t a^4 \sum\limits_{n\in\Lambda} \bar{\psi}_n \frac{i}{4a} \gamma^0 \left( 2\psi_{n} - U^0_n U^0_{n+\hat{0}}  \psi_{n+2\hat{0}} - U^{0\dagger}_{n-\hat{0}}U^{0\dagger}_{n-2\hat{0}} \psi_{n-2\hat{0}}\right),  \\
  \mathcal{O}_{33}:\quad S^{5 B} =&\ c_B a^4 \sum\limits_{n\in\Lambda} \bar{\psi}_n 
  \sum\limits_{j,k,l=1}^3 \epsilon^{jkl} \gamma^5\gamma^j \widehat{F}^{kl}_n \psi_{n}.
 \end{align}

\begin{equation}
  \det (D^{f}_{+\zeta}[U]) = \det (D^{f}_{+\zeta}[U] C^{-1} C) = \det (C D^{f}_{+\zeta}[U] C^{-1}) = \det (D^{f}_{-\zeta}[U^c])^T,
  \label{eq: cc of determinant}
\end{equation}

