\begin{equation}
{\text{d}} s^2= c^2 {\text{d}} t^2-\left( {\text{d}} r + \sqrt{\frac{r_S}{r}}c {\text{d}} t\right)^{2} - r^2{\text{d}}\Omega^2,
\label{schw}
\end{equation}

