One way to calculate the stress components during the simulation,
taking into account the periodic boundary conditions, requires to
explicitly write the forces as derivatives with respect to the
relative vectors ${\ensuremath{\mathbf{r}}}_{kj}$ and ${\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}$. As
\eqref{eq:Vso2Body} depends on ${\ensuremath{\mathbf{r}}}_{kj}$ (there is no ${\ensuremath{s}}(k)$
lath to take into account here), then the derivative with respect
to either $k$ or $j$ is: 

\begin{eqnarray} \nonumber -{\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{R}}}_i}}}} \left(
\frac{{\ensuremath{\mathbf{n}}}_{k,j,k}\cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|}\right)^{2l} &=& \left[ \frac{4l
    ({\ensuremath{\mathbf{n}}}_{k,j,k}\cdot {\ensuremath{\mathbf{r}}}_{k,j})^{2l -1
  }}{(|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|)^{2l}} {\ensuremath{\mathbf{n}}}_{k,j,k}\right.\\ &-&  
  \left. \frac{2l({\ensuremath{\mathbf{n}}}_{k,j,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j})^{2l}}{(|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|)^{2l+1}}\left(|{\ensuremath{\mathbf{r}}}_{k,j}| {\ensuremath{\mathbf{\hat{n}}}}_{k,j,k} + |{\ensuremath{\mathbf{n}}}_{k,j,k}| {\ensuremath{\mathbf{\hat{r}}}}_{k,j}\right)\right](\delta_{i,k} -
\delta_{i,j}). 
\end{eqnarray}

\noindent In the case that the potential is \eqref{eq:Vso3Body},
the derivative with respect to the coordinates of lath $k$ is:
\begin{eqnarray} {\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{R}}}_i}}}}
\left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|}\right)^{2l} = {\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{r}}}_{kj}}}}}
\left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|}\right)^{2l} {\ensuremath{\frac{\partial {{\ensuremath{\mathbf{r}}}_{kj}}}{\partial {{\ensuremath{\mathbf{R}}}_k}}}} + {\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{r}}}_{k}}}}} \left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot
  {\ensuremath{\mathbf{r}}}_{k,j}}{|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{\mathbf{r}}}_{k,j}|}\right)^{2l} {\ensuremath{\frac{\partial {{\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}}}{\partial {{\ensuremath{\mathbf{R}}}_k}}}}, \end{eqnarray}

\noindent Obtaining:
\begin{eqnarray}
{\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{r}}}_{kj}}}}}
\left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|n_{k,j,k}||r_{k,j}|}\right)^{2l}  &=& \nonumber \frac { 2l
  [ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}]^{2l-1}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|]^{2l}} 
{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} -
\frac {2l {\ensuremath{[ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{\mathbf{r}}}_{kj}]}}^{2l}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|]^{2l+1}}
  |{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}| {\ensuremath{\mathbf{\hat{r}}}}_{k,j} 
\\ \\
{\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}}}}}
\left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|n_{k,j,k}||r_{k,j}|}\right)^{2l} &=&
\nonumber \frac { 2l [ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}]^{2l-1}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|]^{2l}} {\ensuremath{\mathbf{n}}}_{k,j,k} - \frac {2l {\ensuremath{[ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{\mathbf{r}}}_{kj}]}}^{2l}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|]^{2l+1}}
|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}| {\ensuremath{\mathbf{\hat{n}}}}_{{\ensuremath{s}}(k),k,k},\\
\end{eqnarray}

\noindent and :${\ensuremath{\frac{\partial {{\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}}}{\partial {{\ensuremath{\mathbf{R}}}_k}}}} = 1$,
${\ensuremath{\frac{\partial {{\ensuremath{\mathbf{r}}}_{kj}}}{\partial {{\ensuremath{\mathbf{R}}}_k}}}} =-1$. The derivative with respect to
$j$,$k$ or ${\ensuremath{s}}(k)$ is:
\begin{eqnarray} \nonumber \nonumber -{\ensuremath{\frac{\partial {}}{\partial {{\ensuremath{\mathbf{R}}}_i}}}} \left(\frac{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}}{|n_{k,j,k}||r_{k,j}|}\right)^{2l} &=& \nonumber \frac { 2l
  [ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}]^{2l-1}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|]^{2l}} \left( {\ensuremath{\mathbf{n}}}_{j,k,k} (\delta_{i,k}-\delta_{i,{\ensuremath{s}}(k)}) + {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} (\delta_{i,k} -
\delta_{i,j}) \right)\\ \nonumber&+& \frac {2l
  {\ensuremath{[ \nskk \cdot {\ensuremath{\mathbf{r}}}_{kj}]}}^{2l}}{[|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}||{\ensuremath{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k}}}|]^{2l+1}}\left(|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}| {\ensuremath{\mathbf{\hat{n}}}}_{{\ensuremath{s}}(k),k,k}(\delta_{i,k} - \delta_{i,{\ensuremath{s}}(k)}) - |{\ensuremath{{\ensuremath{\mathbf{n}}}_{{\ensuremath{s}}(k),k,k}}}| {\ensuremath{\mathbf{\hat{r}}}}_{k,j} (\delta_{i,k} - \delta_{i,j} ) \right), \\ \end{eqnarray}



\subsubsection{Torques}
The definition of the torque is:
\begin{eqnarray} \nonumber \mathbf T_{i,stk.} &=& -{\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}} \times {\ensuremath{\frac{\partial {\Phi_S}}{\partial {{\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}}}}}}. \end{eqnarray}

\noindent And given the torque, the evolution of the orientation
vector is given by:

\noindent With $\delta_{u_1}$ related to the flow. Both $V_{o}$ and
$V_{cn}$ depend on the orientation ${\ensuremath{\mathbf{\hat{u}}}}$. 


\begin{eqnarray}
- \left( {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}} \times {\ensuremath{\frac{\partial {{\ensuremath{V_{so}({\ensuremath{\mathbf{r}}}_{k,j}, {\ensuremath{\mathbf{r}}}_{\neig(k),k})}}}}{\partial {{\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}}}}}} \right) \times {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}} 
\nonumber &=& 2l\delta_{i,k} \left(\frac{{\ensuremath{[ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{\mathbf{r}}}_{kj}]}}^{2l-1}}{[|{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}||{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}|]^{2l}}\left(
{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} ({\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}} \cdot {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_k}}) + {\ensuremath{{\ensuremath{\mathbf{n}}}_{k,j,k}}}({\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_k}} \cdot {\ensuremath{{\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}}})\right)\right.\\ &-& \left.
\frac{ {\ensuremath{[ {\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} \cdot {\ensuremath{\mathbf{r}}}_{kj}]}} ^{2l}}{|{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}}|^{2l+2}|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}|^{2l}}{\ensuremath{{\ensuremath{\mathbf{n}}}_{\neig(k),k,k}}} ({\ensuremath{{\ensuremath{\mathbf{r}}}_{{\ensuremath{s}}(k),k}}} \cdot {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_k}})  \right)
\label{eq:3btorqueSov1}
\end{eqnarray}

For the case in which the orientation vector ${\ensuremath{\mathbf{n}}}_k$ is not given
by a stuck lath the potential is $V_{cn}({\ensuremath{\mathbf{\hat{n}}}}_{k,j,k}\cdot {\ensuremath{\mathbf{\hat{r}}}}_{k,j})$, and the analogous calculation gives:
\begin{eqnarray}
&-& \left( {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}} \times {\ensuremath{\frac{\partial {{\ensuremath{V_{so}({\ensuremath{\mathbf{r}}}_{k,j}, {\ensuremath{\mathbf{r}}}_{\neig(k),k})}}}}{\partial {{\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}}}}}} \right) \times {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_i}} =\\
\nonumber &2l& \delta_{i,k} \left(\frac{({\ensuremath{\mathbf{n}}}_{k,j,k}\cdot {\ensuremath{\mathbf{r}}}_{k,j})^{2l-1}}{[|{\ensuremath{\mathbf{n}}}_{k,j,k}||{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}|]^{2l}}
2 {\ensuremath{\mathbf{n}}}_{k,j,k} ({\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}} \cdot {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_k}}) - 
\frac{ ({\ensuremath{\mathbf{n}}}_{k,j,k} \cdot {\ensuremath{\mathbf{r}}}_{k,j}) ^{2l}}{|{\ensuremath{\mathbf{n}}}_{k,j,k}|^{2l+2}|{\ensuremath{{\ensuremath{\mathbf{r}}}_{k,j}}}|^{2l}}{\ensuremath{\mathbf{n}}}_{k,j,k} ({\ensuremath{\mathbf{r}}}_{k,j} \cdot {\ensuremath{{\ensuremath{\mathbf{\hat{{u}}}}}_k}})  \right)
\label{eq:3btorqueSov2}
\end{eqnarray}



