\title{Multichannel optical atomic magnetometer operating in unshielded environment}


\author{Giuseppe Bevilacqua}
\affiliation{DIISM, University of Siena - Italy}

\author{Valerio Biancalana} 
\email{valerio.biancalana@unisi.it}
