\begin{equation}
\psi({\bf x})=u({\bf x})e^{i{\bf k}\cdot{\bf x}}, \quad u({\bf x})=u({\bf x}+{\bf r}),
\label{factorization}
\end{equation}

\begin{equation}
u({\bf x})=\sum_{{\bf g}}\tilde{u}({\bf g})e^{i{\bf g}\cdot{\bf x}},\quad V({\bf x})=\sum_{{\bf g}}\tilde{V}({\bf g})e^{i{\bf g}\cdot{\bf x}},
\label{Fourier-representation}
\end{equation}

\begin{equation}
\left[{\hbar^2\over 2m}\| {\bf k}+{\bf g}\|^2 -E\right]\tilde{u}({\bf g})=-\sum_{{\bf g}'}\tilde{V}({\bf g}-{\bf g}')\tilde{u}({\bf g}').
\label{Schroedinger-equation-crystal2}
\end{equation}

\begin{equation}
\psi({\bf x})=u'({\bf x})e^{i{\bf k}'\cdot{\bf x}}, \,\, u'({\bf x})=u({\bf x})e^{-i{\bf g}_0\cdot{\bf x}},\,\, {\bf k}'={\bf k}+{\bf g}_0.
\label{factorization2}
\end{equation}

