\author[\'E. Ricard]{\'Eric Ricard}
\address{Laboratoire de Math{\'e}matiques Nicolas Oresme,
Universit{\'e} de Caen Normandie,
14032 Caen Cedex, France}
\email{eric.ricard@unicaen.fr}

\thanks{{\it 2010 Mathematics Subject Classification:} 46L51; 47A30.} 
\thanks{{\it Key words:} Noncommutative $L_p$-spaces}


\begin{abstract}
We prove that  for any (trace-preserving) 
conditional expectation ${\mathcal{E}}$ on a noncommutative $L_p$ with $p>2$, $Id-{\mathcal{E}}$  
is a contraction on the positive cone $L_p^+$.
\end{abstract}
