\begin{split}
\frac{dS}{dt} &= - \beta cSI,\\
\frac{dE}{dt} &= \beta cSI - \lambda E,\\ 
\frac{dI}{dt} &= \lambda E - \gamma I,\\
\frac{dR}{dt} &= \gamma I.
\end{split}
\end{equation}

