\begin{equation*}
  \frac{d{\bf x}_i}{dt}
  =
  \sum_{\substack{j=1\\j\ne i}}^n{\bf u}_j({\bf x}_i)
  =
  \sum_{\substack{j\ne i}}^n{\bf u}_j({\bf x}_i),
\end{equation*}

