\begin{pro}\label{equiv-top}
Let $X$ be a set, ${\cal M}\subseteq{\cal P}{^{\prime}}(X)$ and ${\cal U}_i\subseteq{\cal P}{^{\prime}}(X)$, $i=1,2$, be such that $X=\bigcup{\cal U}_1=\bigcup{\cal U}_2$. Let ${\cal O}_i$ be the topology on ${\cal M}$ generated by ${\cal U}_i$, $i=1,2$. Then ${\cal O}_1\equiv{\cal O}_2$ if and only if ${\cal P}_{{\cal O}_1}\equiv{\cal P}_{{\cal O}_2}$.
\end{pro}

{\hspace{-1cm}{\em Proof.}~~} Clearly, if ${\cal O}_1\equiv{\cal O}_2$ then ${\cal P}_{{\cal O}_1}\equiv{\cal P}_{{\cal O}_2}$. Conversely, let ${\cal P}_{{\cal O}_1}\equiv{\cal P}_{{\cal O}_2}$. Obviously, ${\cal U}_i\subseteq{\cal P}_{{\cal O}_i}$ for $i=1,2$. Thus $({\cal P}_{{\cal O}_i})^-_{{\cal M}}$ is a subbase of ${\cal O}_i$, $i=1,2$. Hence ${\cal O}_1\equiv{\cal O}_2$. {\hspace*{\fill} \hbox{\vrule\vbox{\hrule\phantom{o}\hrule}\vrule}} \vspace{2mm}

\begin{fact}\label{zatwob}
Let $X$ be a set, ${\cal M}\subseteq{\cal P}{^{\prime}}(X)$  and ${\cal O}$ be a lower-Vietoris-type topology on ${\cal M}$. Then, for every $C\in{\cal M}$,
we have that $\overline{C}^{({\cal M},{\cal O})}\supseteq\{M\in{\cal M}{\ |\ } M\subseteq C\}=C^+_{\cal M}$ .
\end{fact}

\begin{pro}\label{V^-}
If $(X,{\cal T})$ is a $T_1$-space, ${\cal M}=CL(X)$ (or ${\cal M}$ is a closed base for $X$), ${\cal O}$ is a lower-Vietoris-type topology on ${\cal M}$ and ${\cal T}={\cal T}_{{\cal O}}$ (see Fact \ref{Vietoris-type} for ${\cal T}_{\cal O}$), then ${\cal O}\equiv {\Upsilon}^-_X$ if and only if for every $F\in{\cal M}$ we have that $\overline{F}^{{\cal O}}=F^+$.
\end{pro}

{\hspace{-1cm}{\em Proof.}~~}
It is obvious that $\overline{F}^{{\Upsilon}^-_X}=F^+$, for every $F\in{\cal M}$.

Conversely, let $\overline{F}^{{\cal O}}=F^+$ for every $F\in{\cal M}$. By Fact \ref{Vietoris-type}, for proving that ${\cal O}\equiv{\Upsilon}^-_X$,
it suffices to show that ${\cal P}_{{\cal O}}$ is a base for ${\cal T}$.
Let $F$ be a closed subset of $X$ and $x\not\in F$. Then $\{x\}\not\in\overline{F}^{{\cal O}}$. Hence there exist $U_1,\dots,U_n\in{\cal P}_{{\cal O}}$, such that $\displaystyle\{x\}\in\bigcap_{i=1}^nU_i^-=U$ and $F\not\in U$. Then there exists an $i\in\{1,\dots,n\}$ such that $F\not\in U_i^-$, i.e. $F\cap U_i=\emptyset$. Thus $x\in U_i\subseteq X\setminus F$ and $U_i\in{\cal P}_{\cal O}$. Hence ${\cal P}_{\cal O}$ is a base for ${\cal T}$. Therefore ${\cal P}_{{\cal O}}\equiv{\cal T}$, i.e. ${\cal O}\equiv {\Upsilon}^-_X$ .{\hspace*{\fill} \hbox{\vrule\vbox{\hrule\phantom{o}\hrule}\vrule}} \vspace{2mm}

\begin{cor}\label{kuratovski}
 Let $(X,{\cal T})$ be a $T_1$-space, ${\cal M}=CL(X)$ (or ${\cal M}$ is a closed base for $X$), ${\cal O}$ is a lower-Vietoris-type topology on ${\cal M}$ and ${\cal T}={\cal T}_{{\cal O}}$. Then the Kuratowski operators for ${\cal O}$ and ${\Upsilon}^-_X$ coincide on the singletons of ${\cal M}$ iff  they coincide on every subset of ${\cal M}$.
\end{cor}

\begin{pro}\label{t0}
Let $X$ be a set, ${\cal M}\subseteq{\cal P}{^{\prime}}(X)$, ${\cal O}$ be a lower-Vietoris-type topology on ${\cal M}$. Then $({\cal M},{\cal O})$ is a $T_0$-space if and only if for every $M_1,M_2\in{\cal M}$ with $M_1\not=M_2$, there exist $U_1,\dots,U_n\in{\cal P}_{{\cal O}}$ such that either ($M_1\cap U_i\not=\emptyset$ for any $i=1,\dots,n$ and there exists an $i_0\in\{1,\dots,n\}$ for which $M_2\cap U_{i_0}=\emptyset$) or ($M_2\cap U_i\not=\emptyset$ for any $i=1,\dots,n$ and there exists an $i_0\in\{1,\dots,n\}$ for which $M_1\cap U_{i_0}=\emptyset$).
\end{pro}


